    async function fetchPizza() {
      try {
        const { data } = await axios.get(
          'https://62c2a193876c4700f5296273.mockapi.io/items/' + id,
        );
        setPizza(data);
      } catch (error) {
        alert('ошибка при получении пиццы');
        navigate('/')
