/* eslint-disable */
import React from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { selectCartItemById } from '../redux/Slices/cart/selectors';
import { CartItem } from '../redux/Slices/cart/types';
import { addItem } from '../redux/Slices/cart/slice';

type FullPizzaProps = {
  id: string;
  title: string;
  imageUrl: string;
  price: number;
  count: number;
  types: number[];
  sizes: number[];
};
const typeName = ['тонкое', 'традиционное'];

const FullPizza: React.FC<FullPizzaProps> = ({ id, price, title, imageUrl, types, sizes }) => {
  const dispatch = useDispatch();
  const cartItem = useSelector(selectCartItemById(id));
  const [activeType, setActiveType] = React.useState(0);
  const [activeSize, setActiveSize] = React.useState(0);
  const addedCount = cartItem ? cartItem.count : 0;

  const onClickAdd = () => {
    const item: CartItem = {
      id,
      price,
      title,
      imageUrl,
      types: typeName[activeType],
      sizes: activeSize,
      count: 0
    };
    dispatch(addItem(item));
  };
  const [pizza, setPizza] = React.useState<{
    title: string,
    imageUrl: string,
    price:number
  }>();
  const { idx } = useParams();
  const navigate = useNavigate()

  React.useEffect(() => {
    async function fetchPizza() {
      try {
        const { data } = await axios.get(
          'https://62c2a193876c4700f5296273.mockapi.io/items/' + idx,
        );
        setPizza(data);
      } catch (error) {
        alert('ошибка при получении пиццы');
        navigate('/')
      }
    }
    fetchPizza();
  }, []);
if (!pizza){
    return <>'Загрузка...'</>
}
  return (
    <div className="pizza-block-wrapper">
    <div className="pizza-block">
          <img className="pizza-block__image" src={pizza.imageUrl} alt="Pizza" />
             <h4 className="pizza-block__title">{pizza.title}</h4>
                 <div className="pizza-block__selector">
        <ul>
{/*          
            <li
              key={i}
              onClick={() => setActiveType(typeId)}
              className={activeType === typeId ? 'active' : ''}>
              {typeName[typeId]}
            </li> */}
         
        </ul>
        <ul>
         
            {/* <li
              key={i}
              onClick={() => setActiveSize(size)}
              className={activeSize === size ? 'active' : ''}>
              {pizza.size} см.
            </li> */}
      
        </ul>
      </div>
      <div className="pizza-block__bottom">
        <div className="pizza-block__price"> {pizza.price} ₽</div>
        <button onClick={onClickAdd} className="button button--outline button--add">
          <svg
            width="12"
            height="12"
            viewBox="0 0 12 12"
            fill="none"
            xmlns="http://www.w3.org/2000/svg">
            <path
              d="M10.8 4.8H7.2V1.2C7.2 0.5373 6.6627 0 6 0C5.3373 0 4.8 0.5373 4.8 1.2V4.8H1.2C0.5373 4.8 0 5.3373 0 6C0 6.6627 0.5373 7.2 1.2 7.2H4.8V10.8C4.8 11.4627 5.3373 12 6 12C6.6627 12 7.2 11.4627 7.2 10.8V7.2H10.8C11.4627 7.2 12 6.6627 12 6C12 5.3373 11.4627 4.8 10.8 4.8Z"
              fill="white"
            />
          </svg>
          <span>Добавить</span>
          {addedCount > 0 && <i>{addedCount}</i>}
        </button>
      </div>
    </div>
  </div>
    // <div className="pizza-block-wrapper">
    //   <div className="pizza-block">
    //     <img  src={pizza.imageUrl} alt="Pizza" />
    //     <h4 className="pizza-block__title">{pizza.title}</h4>

    //     <div className="pizza-block__bottom">
    //       <div className="pizza-block__price">цена {pizza.price} ₽</div>
    //     </div>
    //   </div>
    // </div>
  );
}

export default FullPizza;
